package p2021_12_22;

public class Oper01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 산술 연산자 : +, -, *, /, %(나머지)
		// int형과 int형을 산술연산수행하면 int형으로 결과 처리
		int a =10, b = 3, c;
		double z = 10.0, x = 3.0;
		c = a+b;
		System.out.println("a+b="+c);
		System.out.println("a+b="+(a+b));
		System.out.println("a-b="+(a-b));
		System.out.println("a*b="+(a*b));
		System.out.println("a/b="+(a/b));
		System.out.println("a%b="+(a%b));
		System.out.println("z/x="+(z/x));
		
	}

}
