package p2021_12_27;

public class DoWhile01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		do~while문
//	    do{
//	           반복 실행할 문장;
//	    }while(조건식);
		
//do ~ while문으로 '사랑해요' 메세지를 10번 출력하는 프로그램을 작성하세요?		
		
		int i = 1;
		do {
			System.out.println(i+"사랑해요~!!");
			i++;
		}while(i<=10);
			
		
		
	}

}
